import React from "react";
import { useSelector } from "react-redux";

const Home = () => {
  const postData = useSelector((state) => state.posts.data);
  const isLoading = useSelector((state) => state.posts.loading);
  const error = useSelector((state) => state.posts.error);
  console.log(postData);

  if (isLoading) {
    return "loading...";
  }

  if (error) {
    return error;
  }

  function moreText(text) {
    const textTitle = text.slice(0, 12) + "...";
    const textDesc = text.slice(0, 15) + "\nRead More...";
    return [textTitle, textDesc];
  }

  async function getImage(id) {
    const res = await fetch(`https://picsum.photos/200?random=${id}`)
    const img = await res.json()
    return img
  }
  return (
    <>
      <h2 style={{ textAlign: "center", fontSize: "2rem" }}>Home</h2>
      <div className="card-container">
        {postData.map((e, i) => (
          <div className="card" key={i}>
            <div className="card-img">
              <img
                src="https://images.unsplash.com/photo-1682686580950-960d1d513532?ixlib=rb-4.0.3&ixid=M3wxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=387&q=80"
                alt="item_img"
              />
            </div>
            <div className="info">
              <p>
                <span className="bold">User ID:</span> {e.id}
              </p>
              <p>
                <span className="bold">Title:</span> {moreText(e.title)[0]}
              </p>
              <p>
                <span className="bold">Body:</span> {moreText(e.body)[1]}
              </p>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Home;
