import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";

const Home = () => {
  const postData = useSelector((state) => state.posts.data);
  const isLoading = useSelector((state) => state.posts.loading);
  const error = useSelector((state) => state.posts.error);
  const [image, setImage] = useState([]);
  //   console.log(postData);
  //   useEffect(() => {
  //       fetch(`https://picsum.photos/200?random`).then(e => getImage(e.url));

  //   }, [])

  if (isLoading) {
    return (
        <h1>Loading...</h1>
    );
  }

  // setTimeout(
  // ,0)
  async function getImage(params) {
    const res = await fetch(`https://picsum.photos/200?random`);
    console.log(res.url);
    setImage([...image, res.url]);
    // console.log(res)
    return res.url;
  }
  getImage();
  if (error) {
    return error;
  }

  function moreText(text) {
    const textTitle = text.slice(0, 12) + "...";
    const textDesc = text.slice(0, 15) + "\nRead More...";
    return [textTitle, textDesc];
  }

  console.log(image);

  return (
    <>
      <h2 style={{ textAlign: "center", fontSize: "2rem" }}>Home</h2>
      <div className="card-container">
        {postData.map((e, i) => (
          <div className="card" key={i}>
            <div className="card-img">
              <img src={image[i]} alt="item_img" />
            </div>
            <div className="info">
              <p>
                <span className="bold">User ID:</span> {e.id}
              </p>
              <p>
                <span className="bold">Title:</span> {moreText(e.title)[0]}
              </p>
              <p>
                <span className="bold">Body:</span> {moreText(e.body)[1]}
              </p>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Home;
